import React, { useState } from 'react';
import TableRow from './TableRow';

function Table({ onTableCostChange }) {
  const [rows, setRows] = useState([{}]);

  const handleAddRow = () => {
    setRows((prevRows) => [...prevRows, {}]);
  };

  const handleValueChange = (rowCost) => {
    const totalTableCost = rows.reduce((sum, _, index) => sum + rowCost, 0);
    onTableCostChange(totalTableCost);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>T-shirt</th>
            <th>Branch</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((_, index) => (
            <TableRow key={index} onValueChange={handleValueChange} />
          ))}
        </tbody>
      </table>
      <button onClick={handleAddRow}>Add Row</button>
    </div>
  );
}

export default Table;
