import React, { useState } from 'react';
import './App.css';
import Table from './components/Table';
import TotalTable from './components/TotalTable';

function App() {
  const [totalCost, setTotalCost] = useState(0);
  const [table1Cost, setTable1Cost] = useState(0);
  const [table2Cost, setTable2Cost] = useState(0);

  const handleTable1CostChange = (cost) => {
    setTable1Cost(cost);
    setTotalCost(cost + table2Cost);
  };

  const handleTable2CostChange = (cost) => {
    setTable2Cost(cost);
    setTotalCost(cost + table1Cost);
  };

  return (
    <div className="App">
      <Table onTableCostChange={handleTable1CostChange} />
      <Table onTableCostChange={handleTable2CostChange} />
      <TotalTable cost={totalCost} />
    </div>
  );
}

export default App;
