import React, { useState } from 'react';

function TableRow({ onValueChange }) {
  const shirtValues = {'S': 1, 'M': 2, 'L': 3};
  const branchValues = {'A': 4, 'B': 5, 'C': 6};

  const [shirtSize, setShirtSize] = useState('S');
  const [branch, setBranch] = useState('A');

  const handleShirtChange = (e) => {
    setShirtSize(e.target.value);
    onValueChange(shirtValues[e.target.value] + branchValues[branch]);
  };

  const handleBranchChange = (e) => {
    setBranch(e.target.value);
    onValueChange(shirtValues[shirtSize] + branchValues[e.target.value]);
  };

  return (
    <tr>
      <td>
        <select value={shirtSize} onChange={handleShirtChange}>
          <option value="S">S</option>
          <option value="M">M</option>
          <option value="L">L</option>
        </select>
      </td>
      <td>
        <select value={branch} onChange={handleBranchChange}>
          <option value="A">A</option>
          <option value="B">B</option>
          <option value="C">C</option>
        </select>
      </td>
    </tr>
  );
}

export default TableRow;
